export default async function ({ execution_id, action_execution_id }) {

  const { input } = await fetch(`/platform/automation/v1/executions/${execution_id}`).then((res) => res.json());
  let data1 = ""
  let workflow1 = ""
  // import workflow

  const grant_type = encodeURIComponent("client_credentials");
  const scope = encodeURIComponent("iam-policies-management iam:policies:write iam:policies:read iam:bindings:write iam:bindings:read iam:effective-permissions:read iam:service-users:use iam:limits:read iam:boundaries:read iam:boundaries:write account-idm-read iam:users:read iam:groups:read account-idm-write account-env-read account-env-write account-uac-read account-uac-write");
  //const scope = encodeURIComponent("automation:workflows:read automation:workflows:write");
  const client_id = encodeURIComponent(input["dt_oauth_client_id"]);
  const client_secret = encodeURIComponent(input["dt_oauth_client_secret"]);

  const options = {
    method: "POST",
    headers: {
      'content-type': "application/x-www-form-urlencoded",
    },
      body: "grant_type=" + grant_type + "&scope=" + scope + "&client_id=" + client_id + "&client_secret=" + client_secret
  };

  let ssourl = ""
  var url = getEnvironmentUrl()
  if( url.indexOf('sprint') >= 0){
   ssourl = "https://sso-sprint.dynatracelabs.com/sso/oauth2/token"
  } else {
   ssourl="https://sso.dynatrace.com/sso/oauth2/token"
  }
  console.log(ssourl);

  const resp = await fetch(ssourl,options);
  console.log(resp.status);


  const resp_json = await resp.json();
  const bearer_token = await resp_json['access_token'];
  return { "token": bearer_token, "policy_workflow": workflow1 };
}

