import { execution } from '@dynatrace-sdk/automation-utils';
import { problemsClient } from '@dynatrace-sdk/client-classic-environment-v2';
import { getEnvironmentUrl } from "@dynatrace-sdk/app-environment";

export default async function ({ executionId }) {
  // your code goes here
  const { input } = await fetch(`/platform/automation/v1/executions/${executionId}`).then((res) => res.json());


  console.log(input)
  console.log(input.display_id)


  const data = await problemsClient.getProblem({
    problemId: ""+input['event.id']+""
  });
  //console.log(data)
  // Leave comment


  // calculate jira impact level
  let impact = "Lowest"; // possible jira impact values Lowest, Low, Medium, High, Highest
  let affected_users: number = 0;

    // calculate amount of impacted users
  for (let impacts of data.impactAnalysis.impacts) {

  affected_users = affected_users + impacts.estimatedAffectedUsers
  console.log(affected_users);


  }

  // if impactLevel equals APPLICATION and severityLevel equals AVAILABILITY and no affected users set priority to high
  if (data.impactLevel == "APPLICATION" && data.severityLevel == "AVAILABILITY" && affected_users == 0) {
  impact = "High";    
  }
  // if impactLevel equals APPLICATION and severityLevel equals AVAILABILITY and affected users set priority to highest
  if (data.impactLevel == "APPLICATION" && data.severityLevel == "AVAILABILITY" &&  affected_users > 0) {
  impact = "Highest";     
  }

  if (data.impactLevel == "APPLICATION" && data.severityLevel == "RESOURCE_CONTENTION" &&  affected_users > 0) {
  impact = "Highest";     
  }

  if (data.impactLevel == "APPLICATION" && data.severityLevel == "RESOURCE_CONTENTION" && affected_users == 0) {
  impact = "High";     
  }




  // with a focus on Business Resilience we always chose the highest priority if end users are affected
  if (affected_users > 0) {
  impact = "Highest";     
  }

  // add jira impact to problem details
  data["jira_impact"] = impact;

  console.log("Problem Impact Level: "+data.impactLevel);
  console.log("Problem Severity Level: "+data.severityLevel);
  console.log("Number of affected users: "+affected_users);
